<?php if (!defined('THINK_PATH')) exit();?><!doctype html><html lang="zh-CN"><head><!-- 设置字符编码和手机端--><meta charset="utf-8" /><meta name="viewport" content="width=device-width,initial-scale=1,user-scalable=0" /><!-- 设置收藏小图标 --><link href="__PUBLIC__/image/zm_icon.png" type="image/png" rel="shortcut icon"><!-- 加载jquery mobile --><link href="__PUBLIC__/css/jquery.mobile-1.4.5.css"  rel="stylesheet"><script type="text/javascript" src="__PUBLIC__/js/jquery-1.11.2.js"></script><script type="text/javascript" src="__PUBLIC__/js/jquery.mobile-1.4.5.js"></script><!-- 加载CSS文件 --><link href="__PUBLIC__/css/zm_chat.css"  rel="stylesheet"><!-- 加载JS文件 --><script type="text/javascript" src="__PUBLIC__/js/zm.js"></script><title>仲明互助交流首页</title></head><body><div data-role="page" ><div data-role="header" data-theme="b"><h1>请你帮帮忙</h1></div><section class="beHelpInfo"><div class="beHelpPic"><div><img class="chatpic" src="__PUBLIC__/image/default_avatar.jpeg" alt="好友头像"/></div><?php if($helpInfo["user_id"] != $_SESSION['userId']): ?><div class="answerButton"><button data-role="none">我来答</button></div><?php else: endif; ?></div><div class="chatContent questionContent"><?php echo ($helpInfo["blog_content"]); ?><div class="time"><?php echo ($helpInfo["blog_time"]); ?></div></div><div class="blank"></div></section><section class="helpBigBox"><div class="answerInput"><div class="inputBigBox" ><textarea rows="4" cols="20" data-role="none" name="" id="" class="inputBox" ></textarea><button class="helpButton" data-role="none" id="helpHim" beHelpId="<?php echo ($helpInfo["user_id"]); ?>">提交</button><div class="blank"></div></div></div></section><section class="answerRecord"><?php if(empty($helpList)): ?><span style="padding: 5px;margin-left: 10px;">你知道吗？帮帮他吧</span><?php else: if(is_array($helpList)): $k = 0; $__LIST__ = $helpList;if( count($__LIST__)==0 ) : echo "$empty" ;else: foreach($__LIST__ as $key=>$item): $mod = ($k % 2 );++$k;?><div class="oneAnswerRecord"><div class="answerContent"><?php